const lanContext =createContext();

export default function Header(){

const [language ,setLanguage]=useState('Hello World')
    return(
        <>
         <lanContext.Provider value={language}>

         <Body language={language}/>
         </lanContext.Provider>
          

         {/* <button onClick={()=>Body setLanguage={language}}> Click me</button> 
         */}
        
        {/* <button onClick={language={language}}>Arabic</button>
        <button>English</button>
        <button>Japanese</button> */}
        </>
    )
}


function Body({lan}){
    const lang =useContext()
    return(
        <>
<h2>{`Hey im in body ${lan}`}</h2>

        </>
    )
}
