 import React from 'react'
import './App.css';


const employeeContext=React.createContext();

export default  class App extends React.Component{

  constructor(props){
    super(props);
      this.state={

        id:102,
        Name:'Shahad',
        Location:'Riyadh',
        Salary:20000
};
  }

  render(){

return(
  <>
  <h1>This is class App</h1>

<p>Employee ID is :{this.state.Name}</p>

  <employeeContext.Provider value={this.state}>
  <Employee/>
  </employeeContext.Provider>

  </>
)
  }
}
////////////////////
class Employee extends React.Component{

  static context =employeeContext;


  render(){
    return(
      <>
        <h2>This is class Employee</h2>
   <p>Employee ID is : {this.context.Name}</p>
        <Salary/>
      </>
    )
  }
}
 ////////////////////
 class Salary extends React.Component{

  static context=employeeContext;
   render(){
     return(
       <>
               <h3>This is class Salary</h3>
               <p>Employee ID is : {this.context.id}</p>
       </>
     )
   }
 }
