import React, { createContext, useState ,useContext} from "react";


const lanContext =createContext();

export default function Header(){

const [language ,setLanguage]=useState('HelloWorld')
    return(

        <>
        <button 
        type='button'
        onClick={()=>setLanguage('Hello World')}>
        
         English </button>

         <button 
        type='button'
        onClick={()=>setLanguage('السلام عليكم')}>
        
         Arabic </button>

         <button 
        type='button'
        onClick={()=>setLanguage('Hello World')}>
        
         English </button>

         <button 
        type='button'
        onClick={()=>setLanguage('Hello World')}>
        
         English </button>


         <lanContext.Provider value={language}>

         <Body language={language}/>

         </lanContext.Provider>
          

          
        </>
    )
}


function Body({lan}){
    
    const lang =useContext(lanContext);

    if (lang ==='Hello World'){
    return(
        <>
<h2>{`${lang}`}</h2>

        </>
    )}
    else if(lang ==='السلام عليكم')
    return(

        <h2>{`${lang}`}</h2>
    )
}
